  Token skipTo(int skip) throws ParseException {
        Token current = token;
        Token token ;
        StringBuffer image = new StringBuffer();

        for(;;)
        {
                token = getToken(1);
                if (token.kind == EOF)
                        unexpectedEof(current);
                if (token.kind == skip)
                {
                        getNextToken();
                        break ;
                }

                image.append(token.image);
                getNextToken();
        }
        token.image = image.toString();
        return token ;
  }

  Token skipToOr(int skip1, int skip2) throws ParseException {
        Token current = token;
        Token token ;
        StringBuffer image = new StringBuffer();

        for(;;)
        {
                token = getToken(1);
                if (token.kind == EOF)
                        unexpectedEof(current);
                if (token.kind == skip1 || token.kind == skip2)
                {
                        // preserve skip token
                        break ;
                }

                image.append(token.image);
                getNextToken();
        }
        token.image = image.toString();
        return token ;
  }

  void skipParen() throws ParseException {
        Token current = token;
        Token token;
        int nesting = 1 ;
        for(;;)
        {
                token = getToken(1);
                if (token.kind == EOF)
                        unexpectedEof(current);
                if (token.kind == LPAREN || token.kind == LBRACKET)
                        nesting++;
                if (token.kind == RPAREN || token.kind == RBRACKET)
                {
                        nesting--;
                        if ( nesting == 0 )
                                break;
                }
                getNextToken();
        }
  }

  void skipToEndcase() throws ParseException {
        Token current = token;
        Token token ;
        int nesting = 1 ;
        for(;;)
        {
                token = getToken( 1 );
                if ( token.kind == EOF )
                        unexpectedEof(current);
                if ( token.kind == CASE )
                        nesting++;
                if ( token.kind == ENDCASE )
                {
                        nesting--;
                        if ( nesting == 0 )
                        {
                                getNextToken();
                                break;
                        }
                }
                getNextToken();
        }
  }

